@import url('https://fonts.googleapis.com/css2?family=Archivo+Black&family=Space+Grotesk:wght@500;700&display=swap');

:root {
    --bg: #FFED00;
    --text: #000000;
    --accent: #FF5C00;
    --white: #FFFFFF;
    --border-size: 4px;
    --shadow-offset: 8px;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    background-color: var(--bg);
    color: var(--text);
    font-family: 'Space Grotesk', sans-serif;
    overflow-x: hidden;
    border: var(--border-size) solid var(--text);
}

/* Navigation */
.nav-bar {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1.5rem 3rem;
    border-bottom: var(--border-size) solid var(--text);
    background: var(--white);
}

.logo {
    font-family: 'Archivo Black', sans-serif;
    font-size: 2rem;
    text-transform: uppercase;
}

.nav-links a {
    text-decoration: none;
    color: var(--text);
    font-weight: 700;
    margin-left: 2rem;
    text-transform: uppercase;
}

.cta-btn {
    background: var(--accent);
    padding: 0.8rem 1.5rem;
    border: var(--border-size) solid var(--text);
    box-shadow: 4px 4px 0px var(--text);
    transition: 0.2s;
}

.cta-btn:hover {
    box-shadow: 0px 0px 0px var(--text);
    transform: translate(4px, 4px);
}

/* Hero Section */
.hero {
    padding: 5rem 3rem;
    text-align: center;
    position: relative;
}

.hero-title {
    font-family: 'Archivo Black', sans-serif;
    font-size: clamp(4rem, 15vw, 12rem);
    line-height: 0.9;
    margin-bottom: 2rem;
}

.outline {
    -webkit-text-stroke: 3px var(--text);
    color: transparent;
}

.hero-image-container {
    position: relative;
    display: inline-block;
    margin: 2rem 0;
}

.coffee-box {
    border: var(--border-size) solid var(--text);
    background: var(--white);
    box-shadow: var(--shadow-offset) var(--shadow-offset) 0px var(--text);
    padding: 1rem;
}

.coffee-box img {
    max-width: 400px;
    display: block;
    filter: grayscale(100%) contrast(150%);
}

.badge {
    position: absolute;
    top: -20px;
    right: -20px;
    background: var(--accent);
    color: var(--white);
    padding: 1rem;
    font-weight: 900;
    border: var(--border-size) solid var(--text);
    transform: rotate(15deg);
}

.hero-subtext {
    font-size: 1.5rem;
    font-weight: 700;
    max-width: 600px;
    margin: 2rem auto;
    background: var(--white);
    border: var(--border-size) solid var(--text);
    padding: 1rem;
}

/* Cards */
.menu {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 2rem;
    padding: 3rem;
}

.card {
    background: var(--white);
    padding: 2rem;
    border: var(--border-size) solid var(--text);
    box-shadow: var(--shadow-offset) var(--shadow-offset) 0px var(--text);
    transition: 0.3s;
}

.card:hover {
    transform: translate(-5px, -5px);
    box-shadow: 13px 13px 0px var(--text);
}

.card.active {
    background: var(--accent);
}

.price {
    display: inline-block;
    margin-top: 1rem;
    font-size: 2rem;
    font-weight: 900;
}

/* Marquee */
.marquee {
    background: var(--text);
    color: var(--white);
    padding: 1rem 0;
    overflow: hidden;
    white-space: nowrap;
    border-top: var(--border-size) solid var(--text);
}

.marquee-content {
    display: inline-block;
    font-family: 'Archivo Black', sans-serif;
    font-size: 2rem;
    animation: scroll 20s linear infinite;
}

@keyframes scroll {
    from { transform: translateX(0); }
    to { transform: translateX(-50%); }
}

@media (max-width: 768px) {
    .nav-bar { flex-direction: column; gap: 1rem; }
    .hero-title { font-size: 4rem; }
    .coffee-box img { max-width: 100%; }
}

document.addEventListener('DOMContentLoaded', () => {
    // Register GSAP ScrollTrigger
    gsap.registerPlugin(ScrollTrigger);

    // Hero Title Animation
    gsap.from('.hero-title', {
        duration: 1.2,
        y: 100,
        opacity: 0,
        ease: "expo.out"
    });

    // Floating Animation for the coffee image
    gsap.to('.coffee-box', {
        y: -20,
        duration: 2,
        repeat: -1,
        yoyo: true,
        ease: "power1.inOut"
    });

    // Staggered Menu Cards Reveal
    gsap.from('.card', {
        scrollTrigger: {
            trigger: '.menu',
            start: "top 80%"
        },
        x: -100,
        opacity: 0,
        stagger: 0.2,
        duration: 0.8,
        ease: "back.out(1.7)"
    });

    // Hover sound effect simulation (visual only)
    const cards = document.querySelectorAll('.card');
    cards.forEach(card => {
        card.addEventListener('mouseenter', () => {
            gsap.to(card, { scale: 1.02, duration: 0.1 });
        });
        card.addEventListener('mouseleave', () => {
            gsap.to(card, { scale: 1, duration: 0.1 });
        });
    });
});

